sql.where:
i.status != 4

  and i.title like '%${query.title}%'


  and i.status = #{query.status}

mapper.select:
SELECT FROM tb_item i LEFT JOIN tb_item_cat c ON i.cid = c.id left JOIN tb_user u on i.uid = u.id left JOIN tb_item_activity a on i.aid = a.id
${i}
ORDER BY i.id DESC
limit #{page.offset},#{page.rows}
